def step(s, a):
  r = 0

  if a == "hit":
    s[1] = sum(hit(s[1]))

    if s[1] &lt; 1 or s[1] &gt; 21:
      r = -1

    return {"s": s, "r": r}

  if a == "stick":
    if s[0] &gt;= 17:
      if s[0] &lt; 1 or s[0] &gt; 21:
        r = 1
      elif s[1] &gt; s[0]:
        r = 1
      elif s[0] &gt; s[1]:
        r = -1

      return {"s": s, "r": r}

    else:
      s[0] = sum(hit(s[0]))
      s = step(s, "stick")
      return s

# play first card and hit
s1 = step(s = [drawCard("black"), drawCard("black")], a = "hit")
s1

